import React,{useEffect,useState} from 'react';
import { useParams } from 'react-router-dom';
import IITKGPLogo from "../../images/IIT_Kharagpur_Logo.png";
import Trail from "../../images/WebsiteHackathon.jpg";
import './CertifPreview.css';
import Popper from '../../components/Popper';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';

//https://gymkhana.iitkgp.ac.in/certifgen/view/NjYxYWFlOGM4ZjU0NTJjZmNlM2IxZjIx
//data:image/png;base64,
const CertifPreview = () =>{
    const params = useParams();
    const [certificateImage, setCertificateImage] = useState('');
    const [certifData,setCertifData] = useState({});
    useEffect(()=>{
        const id = params.certifid;
        console.log(id);
        const getCertifData = async () => {
            try {
                const response = await fetch(`http://localhost:5050/api/certifgen/generateCertif/${id}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch certificate data');
                }
                const data = await response.json();
                setCertifData(data);
    
                // Once data is fetched, download the PDF
                downloadPdf(data);
            } catch (error) {
                console.error('Error fetching certificate data:', error);
            }
        };

        getCertifData();

        const base64Image = 'data:image/jpeg;base64,';
        setCertificateImage(base64Image);
    },[])

    const downloadImage = () => {
        // Create a Blob from the base64 encoded image
        const byteCharacters = atob(certificateImage.split(',')[1]);
        const byteNumbers = new Array(byteCharacters.length);
        for (let i = 0; i < byteCharacters.length; i++) {
            byteNumbers[i] = byteCharacters.charCodeAt(i);
        }
        const byteArray = new Uint8Array(byteNumbers);
        const blob = new Blob([byteArray], { type: 'image/jpeg' });

        // Create a download link and trigger the download
        const url = window.URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', 'certificate.jpg');
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    };

    // const downloadPdf = () => {
    //     const pdf = new jsPDF('p', 'mm', 'a4');

    //     // Calculate the dimensions of the image relative to the page size
    //     const pageWidth = pdf.internal.pageSize.getWidth();
    //     const pageHeight = pdf.internal.pageSize.getHeight();
    
    //     // Load the image to get its natural width and height
        // const img = new Image();
        // img.src = certificateImage;
    //     img.onload = () => {
    //         const imgWidth = pageWidth;
    //         const imgHeight = (pageWidth * img.naturalHeight) / img.naturalWidth;
    
    //         // Add the image to the PDF
    //         pdf.addImage(certificateImage, 'JPEG', 0, 0, imgWidth, imgHeight);
    
    //         // Save the PDF
    //         pdf.save('certificate.pdf');
    //     };
    // };
    const downloadPdf = () => {
        const img = new Image();
        img.onload = () => {
            const pdf = new jsPDF('p', 'pt', [img.width, img.height]);
            // Set the width of the image to match the width of the PDF page
            const imgWidth = pdf.internal.pageSize.getWidth();
            const imgHeight = (img.height * imgWidth) / img.width;
    
            // Add the image to the PDF
            pdf.addImage(img, 'JPEG', 0, 0, imgWidth, img.height);
    
            // Save the PDF
            pdf.save('certificate.pdf');
        };
        img.src = certificateImage;
    };
    
    
    
    
    


    return(
        <div className='certifpreview' style={{maxHeight:"100%"}}>
            <div className='certifpreview_nav' style={{display:"flex",alignItems:"center",width:"100vw",justifyContent:"center",gap:"0.75rem"}}>
                <img src="/data/media/images/general/gymkhanaLogo.png" alt="gymk_logo" className='gymk_logo'/>
                <h1>Technology Students' Gymkhana</h1>
                <img src={IITKGPLogo} alt="Kgp_logo" className='kgp_logo'/>
            </div>
            <div className='certifpreview_body' style={{display:"flex",flexDirection:"column",alignItems:"center",width:"80vw",margin:"0 auto"}}>
                <h1 style={{fontSize:"1.75rem"}}>Certificate type</h1>
                <p style={{color:"white",fontSize:"1.75rem"}}>Event Name</p>    
                <p style={{color:"white",fontSize:"1.1rem"}}>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec nec fringilla sem. Mauris fringilla, diam sit amet cursus consectetur, est quam vehicula turpis, in faucibus diam justo feugiat turpis. Nunc ac suscipit mauris. Quisque nec tempus ipsum. Interdum et malesuada fames ac ante ipsum primis in faucibus. Nunc viverra mauris tortor, in convallis augue ultrices a.</p>
                <div className='certificateimage'>
                    <img src={Trail} alt="Certificate"/>
                    <div className='download_btns' style={{display:"flex",justifyContent:"space-between",padding:"1rem 0"}}>
                        <button className='downloadBtn' onClick={downloadImage} ><i class="fa fa-picture-o" aria-hidden="true"></i>Download as Image</button>
                        <button className='downloadBtn'onClick ={downloadPdf}><i class="fa fa-file-pdf-o" aria-hidden="true"></i>Download as Pdf</button>
                    </div>
                </div>
                {/* <div className='certifpreview_share'>
                    <p className='certifshare_text'>Share it on:</p>
                    <div className='certifshare_links'>
                        <a href="#" className='certifshare_link'><i class="fa fa-facebook"></i></a>
                        <a href="#" className='certifshare_link'><i class="fa fa-twitter"></i></a>
                        <a href="#" className='certifshare_link'><i class="fa fa-instagram"></i></a>
                        <a href="#"className='certifshare_link'><i class="fa fa-linkedin"></i></a>
                    </div>
                </div> */}
            </div>
            <Popper />
        </div>
    )
}

export default CertifPreview;